mapper.select:
SELECT memberId, memberPw, memberName, memberEmail, memberContact, memberAgree, joinDate 
FROM account_member

	
		memberId = #{memberId}
	
	
		AND memberName = #{memberName}
	
	
		AND memberEmail = #{memberEmail}
	
	
		AND memberPw = #{memberPw}

SELECT memberId, memberPw, memberName, memberEmail, memberContact, memberBirth, memberAgree, joinDate 
FROM account_faver 
WHERE memberId = #{memberId} 

	AND memberPw = #{memberPw}

SELECT memberId, memberPw, memberName, memberEmail, memberContact, restauName, restauAddr, memberAgree
FROM account_favee 
WHERE memberId = #{memberId} 

	AND memberPw = #{memberPw}

mapper.update:
UPDATE account_member

	memberPw = #{newPw},
	memberEmail = #{newEmail},
	memberContact = #{newPhone}

WHERE memberId = #{newId}

mapper.select:
SELECT rowNum, memberId, bullet, title, content, status, createdate, isdelete
FROM account_bbs
WHERE isdelete = 'N'
	AND memberId = #{memberId}
	AND bullet = #{bullet}
	AND status = #{status}
	AND rowNum = #{rowNum}

SELECT rowNum, memberId, bullet, title, content, status
FROM account_bbs
WHERE rowNum = #{rowNum}

mapper.update:
UPDATE account_bbs

	bullet = #{bullet},
	title = #{title},
	content = #{content},
	memberId = #{memberId}

WHERE rowNum = #{rowNum}

mapper.delete:
UPDATE account_bbs
SET isdelete = 'Y', status = '문의삭제'
WHERE rowNum = #{rowNum} AND memberid = #{memberId}
