mapper.select:
SELECT rowNum, memberBid, memberName, restauName, restauAddr, reservationDate, personnel, status, memberOid, charge, reservationNum
FROM account_reservation

	memberBid = #{memberBid}
	AND memberOid = #{memberOid}
	AND status = #{status}
	AND (reservationDate BETWEEN #{reservationDate} AND #{reservationDate2})

mapper.update:
UPDATE account_reservation 
SET status = #{status} 
WHERE reservationNum = #{reservationNum}
